import React, { useState, useEffect } from "react";
import axios from "axios";
import Main from "../../Templates/Main/Main";
import "./ConsultaPersonalizada.css";

const ConsultaPersonalizada = () => {
  const [state, setState] = useState([]);
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    getEmpresasDb();
  }, []);

  async function getEmpresasDb() {
    try {
      const resp = await axios.get(
        "http://127.0.0.1:5001/export-personalizada"
      );
      resp.data.map((empresa) => {
        setState((prevstate) => [
          ...prevstate,
          { nome: empresa["empresa"], preco: empresa["price"] },
        ]);
      });
    } catch {
      console.log("erro na api");
      setState([{ nome: "try again", preco: "" }]);
    }
  }

  useEffect(() => {
    console.log(toggle);
  }, [toggle]);

  function renderToggle() {
    if (toggle)
      return (
        <div className="options">
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id="defaultCheck1"
            />
            <label className="form-check-label" for="defaultCheck1">
              Default checkbox
            </label>
          </div>

          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              value=""
              id="defaultCheck2"
              disabled
            />
            <label className="form-check-label" for="defaultCheck2">
              Disabled checkbox
            </label>
          </div>
        </div>
      );
    return null;
  }

  return (
    <Main>
      <button onClick={() => setToggle(!toggle)}>
        <i className="fa fa-list" aria-hidden="true"></i>
      </button>
      {renderToggle()}
      <div className="dashboard">
        <div className="empresas">
          {state.map((empresa) => (
            <button key={empresa.nome}>
              {empresa.nome}
              <br />
              {empresa.preco}
            </button>
          ))}
        </div>
      </div>
    </Main>
  );
};

export default ConsultaPersonalizada;
