import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Main from "../../Templates/Main/Main";
import "./ConsultaPersonalizada.css";

const ConsultaPersonalizada = () => {
  const [state_price, setStatePrice] = useState([]);
  const [state_no_Price, setStateNoPrice] = useState([]);
  const [toggle, setToggle] = useState(false);
  const checkEl = useRef(null);

  useEffect(() => {
    getEmpresasDb();
  }, []);
  useEffect(() => {
    console.log(toggle);
  }, [toggle]);
  useEffect(() => {
    console.log(state_no_Price);
  }, [state_no_Price]);

  async function getEmpresasDb() {
    try {
      const resp = await axios.get(
        "http://127.0.0.1:5001/export-personalizada"
      );
      resp.data.map((empresa) => {
        setStatePrice((prevstate) => [
          ...prevstate,
          {
            nome: empresa["empresa"],
            cod_empresa: empresa["cod_empresa"],
            preco: empresa["price"],
          },
        ]);
      });
    } catch {
      console.log("erro na api");
      setStateNoPrice([{ nome: "try again", preco: "" }]);
    }
  }
  async function pushEmpresasDb() {
    try {
      const resp = await axios.post(
        "http://127.0.0.1:5001/save-personalizada",
        state_no_Price
      );
      alert(resp);
    } catch {
      alert("Empresa nÃ£o cadastrada");
    }
  }

  const handleInputCheck = (e) => {
    if (e.target.checked) {
      state_price.map((empresa) => {
        if (empresa["cod_empresa"] != checkEl.current.getAttribute("nome")) {
          setStateNoPrice((prevstate) => [
            ...prevstate,
            {
              empresa: checkEl.current.getAttribute("nome"),
              cod_empresa: checkEl.current.getAttribute("cod"),
            },
          ]);
        }
      });
    } else {
      return null;
    }
  };

  function renderToggle() {
    if (toggle)
      return (
        <div className="options">
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              cod="PETR4.SA"
              nome="Petrobras"
              ref={checkEl}
              onClick={handleInputCheck}
            />
            <label className="form-check-label">Petrobras</label>
          </div>

          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              cod="VALE3.SA"
              nome="Vale"
              ref={checkEl}
              onClick={handleInputCheck}
            />
            <label className="form-check-label">Vale</label>
          </div>
          <button type="button"></button>
        </div>
      );
    return null;
  }

  return (
    <Main>
      <button onClick={() => setToggle(!toggle)}>
        <i className="fa fa-list" aria-hidden="true"></i>
      </button>
      {renderToggle()}
      <div className="dashboard">
        <div className="empresas">
          {state_price.map((empresa) => (
            <button key={empresa.nome}>
              {empresa.nome}
              <br />
              {empresa.preco}
            </button>
          ))}
        </div>
      </div>
    </Main>
  );
};

export default ConsultaPersonalizada;
