import React, { useState, useEffect } from "react";
import axios from "axios";
import Main from "../../Templates/Main/Main";
import "./ConsultaPersonalizada.css";

const ConsultaPersonalizada = () => {
  const [state, setState] = useState([]);

  useEffect(() => {
    getEmpresasDb();
  }, []);

  async function getEmpresasDb() {
    try {
      const resp = await axios.get(
        "http://127.0.0.1:5001/export-personalizada"
      );
      resp.data.map((empresa) => {
        setState((prevstate) => [
          ...prevstate,
          { nome: empresa["empresa"], preco: empresa["price"] },
        ]);
      });
    } catch {
      console.log("erro na api");
      setState([{ empresa: "try again", preco: "" }]);
    }
  }

  // useEffect(() => {
  //   console.log(state);
  // }, [state]);

  return (
    <Main>
      <button>
        <i class="fa fa-list" aria-hidden="true"></i>
      </button>
      <div className="dashboard">
        <div className="empresas">
          {state.map((empresa) => (
            <button key={empresa.nome}>
              {empresa.nome}
              <br />
              {empresa.preco}
            </button>
          ))}
        </div>
      </div>
    </Main>
  );
};

export default ConsultaPersonalizada;
