import React, { useState, useEffect } from "react";
import axios from "axios";
import Main from "../../Templates/Main/Main";
import "./ConsultaPersonalizada.css";

const ConsultaPersonalizada = () => {
  const [state, setState] = useState([]);
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    getEmpresasDb();
  }, []);

  async function getEmpresasDb() {
    try {
      const resp = await axios.get(
        "http://127.0.0.1:5001/export-personalizada"
      );
      resp.data.map((empresa) => {
        setState((prevstate) => [
          ...prevstate,
          { nome: empresa["empresa"], preco: empresa["price"] },
        ]);
      });
    } catch {
      console.log("erro na api");
      setState([{ empresa: "try again", preco: "" }]);
    }
  }

  // useEffect(() => {
  //   console.log(state);
  // }, [state]);
  function renderToggle() {
    if (toggle)
      return (
        <form class="dropdown-menu p-4">
          {/* <div class="form-check">
              <input type="checkbox" class="form-check-input" id="dropdownCheck2">
                <label class="form-check-label" for="dropdownCheck2">
                  Remember me
                </label>
            </div>
            <button type="submit" class="btn btn-primary">Sign in</button> */}
        </form>
      );
    return null;
  }

  return (
    <Main>
      {renderToggle()}
      <button onClick={setToggle(!toggle)}>
        <i class="fa fa-list" aria-hidden="true"></i>
      </button>
      <div className="dashboard">
        <div className="empresas">
          {state.map((empresa) => (
            <button key={empresa.nome}>
              {empresa.nome}
              <br />
              {empresa.preco}
            </button>
          ))}
        </div>
      </div>
    </Main>
  );
};

export default ConsultaPersonalizada;
